```java
import java.io.*;
import java.util.*;

public class Main {
    static final int N = 3005;
    static final int M = 3005;
    static final int mod = 1000000007;
    static int n, m;
    static int[] l = new int[M + 5];
    static int[] r = new int[M + 5];
    static char[] s = new char[N + 5];
    static int[][] f = new int[N + 5][N + 5];
    static int[][] g = new int[N + 5][N + 5];
    static int[][] h = new int[N + 5][N + 5];
    static int[][] dp = new int[N + 5][N + 5];

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        PrintWriter pw = new PrintWriter(System.out);
        n = sc.nextInt();
        m = sc.nextInt();
        String str = sc.next();
        for (int i = 1; i <= n; i++) {
            s[i] = str.charAt(i - 1);
        }
        for (int i = 1; i <= m; i++) {
            l[i] = sc.nextInt();
            r[i] = sc.nextInt();
        }
        for (int i = 1; i <= n; i++) {
            f[i][i] = (s[i] == '0' ? 1 : 0);
            g[i][i] = (s[i] == '1' ? 1 : 0);
        }
        for (int len = 2; len <= n; len++) {
            for (int i = 1; i + len - 1 <= n; i++) {
                int j = i + len - 1;
                f[i][j] = (f[i + 1][j] + f[i][j - 1]);
                if (f[i][j] >= mod) f[i][j] -= mod;
                g[i][j] = (g[i + 1][j] + g[i][j - 1]);
                if (g[i][j] >= mod) g[i][j] -= mod;
                h[i][j] = (h[i + 1][j] + h[i][j - 1] + (s[i] == s[j] ? 1 : 0));
                if (h[i][j] >= mod) h[i][j] -= mod;
                if (s[i] == s[j]) h[i][j]--;
            }
        }
        for (int i = 1; i <= m; i++) {
            for (int j = l[i]; j <= r[i] - 1; j++) {
                dp[j][r[i]] = (dp[j][r[i]] + dp[j + 1][r[i]]);
                if (dp[j][r[i]] >= mod) dp[j][r[i]] -= mod;
                dp[j][r[i]] = (dp[j][r[i]] + f[j + 1][r[i]]);
                if (dp[j][r[i]] >= mod) dp[j][r[i]] -= mod;
                dp[j][r[i]] = (dp[j][r[i]] + g[j][r[i] - 1]);
                if (dp[j][r[i]] >= mod) dp[j][r[i]] -= mod;
                dp[j][r[i]] = (dp[j][r[i]] + h[j + 1][r[i] - 1]);
                if (dp[j][r[i]] >= mod) dp[j][r[i]] -= mod;
            }
        }
        int ans = 0;
        for (int i = 1; i <= n; i++) {
            ans = (ans + dp[i][n]);
            if (ans >= mod) ans -= mod;
        }
        pw.println(ans);
        pw.close();
    }
}
