#include <stdio.h>
#include <math.h>

#define N 1000

typedef struct {
    double x, y, r;
} circle;

int ccw(double x1, double y1, double x2, double y2, double x3, double y3) {
    return (x1 - x3) * (y2 - y3) - (x2 - x3) * (y1 - y3);
}

int intersect(circle c1, circle c2, double *x, double *y) {
    double dx = c2.x - c1.x;
    double dy = c2.y - c1.y;
    double d = sqrt(dx * dx + dy * dy);
    if (d > c1.r + c2.r) return 0;
    if (d < abs(c1.r - c2.r)) return 0;
    double a = (c1.r * c1.r - c2.r * c2.r + d * d) / (2 * d);
    double h = sqrt(c1.r * c1.r - a * a);
    *x = c1.x + dx * a / d;
    *y = c1.y + dy * a / d;
    if (dy > 0) {
        *y -= h;
    } else {
        *y += h;
    }
    return 1;
}

int main() {
    double sx, sy, tx, ty;
    int n;
    scanf("%lf%lf%lf%lf%d", &sx, &sy, &tx, &ty, &n);
    circle c[N];
    for (int i = 0; i < n; i++) {
        scanf("%lf%lf%lf", &c[i].x, &c[i].y, &c[i].r);
    }
    double ans = 0;
    for (int i = 0; i < n; i++) {
        double x, y;
        if (intersect(c[i], (circle){tx, ty, 0}, &x, &y)) {
            ans = max(ans, sqrt((x - tx) * (x - tx) + (y - ty) * (y - ty)));
        }
        if (intersect(c[i], (circle){sx, sy, 0}, &x, &y)) {
            ans = max(ans, sqrt((x - sx) * (x - sx) + (y - sy) * (y - sy)));
        }
    }
    double d = sqrt((tx - sx) * (tx - sx) + (ty - sy) * (ty - ty));
    ans = max(ans, d);
    printf("%.10lf\n", ans);
    return 0;
}
