```cpp
#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

using namespace std;

struct Point {
    long long x, y;
};

struct Circle {
    Point center;
    long long radius;
};

bool cmp(const Circle& a, const Circle& b) {
    return a.center.x < b.center.x || (a.center.x == b.center.x && a.center.y < b.center.y);
}

double distance(const Point& p1, const Point& p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

double intersect(const Circle& c1, const Circle& c2) {
    return sqrt(pow(c1.center.x - c2.center.x, 2) + pow(c1.center.y - c2.center.y, 2)) - c1.radius - c2.radius;
}

double intersect(const Circle& c, const Point& p) {
    return sqrt(pow(c.center.x - p.x, 2) + pow(c.center.y - p.y, 2)) - c.radius;
}

double solve(const Circle& c, const Point& p1, const Point& p2) {
    double d1 = intersect(c, p1), d2 = intersect(c, p2);
    if (d1 >= 0 && d2 >= 0) return max(d1, d2);
    if (d1 < 0 && d2 < 0) return 0;
    if (d1 < 0) return d2;
    return d1;
}

double solve(const vector<Circle>& circles, const Point& p1, const Point& p2) {
    double ans = 0;
    for (const auto& c : circles) {
        ans = max(ans, solve(c, p1, p2));
    }
    return ans;
}

int main() {
    int n;
    long long x1, y1, x2, y2;
    cin >> x1 >> y1 >> x2 >> y2 >> n;
    vector<Circle> circles(n);
    for (int i = 0; i < n; i++) {
        cin >> circles[i].center.x >> circles[i].center.y >> circles[i].radius;
    }
    sort(circles.begin(), circles.end(), cmp);
    double ans = solve(circles, {x1, y1}, {x2, y2});
    double d = distance({x1, y1}, {x2, y2});
    cout << d + ans << endl;
    return 0;
}
