        double xm = c1.center.x + a * (c2.center.x - c1.center.x) / d;
        double ym = c1.center.y + a * (c2.center.y - c1.center.y) / d;
        double xs1 = xm + h * (c2.center.y - c1.center.y) / d;
        double xs2 = xm - h * (c2.center.y - c1.center.y) / d;
        double ys1 = ym - h * (c2.center.x - c1.center.x) / d;
        double ys2 = ym + h * (c2.center.x - c1.center.x) / d;
        double d1 = dist(p, new Point(xs1, ys1));
        double d2 = dist(p, new Point(xs2, ys2));
        return Math.min(d1, d2);
    }

    static double solve(ArrayList<Circle> circles, Point start, Point end) {
        double ans = 0;
        for (Circle c : circles) {
            double d1 = intersect(start, c);
            double d2 = intersect(end, c);
            double d3 = intersect(c, c, start);
            double d4 = intersect(c, c, end);
            if (d1 > 0 && d2 > 0 && d3 > 0 && d4 > 0) {
                ans = Math.max(ans, Math.max(d1, d2));
            }
        }
        return ans;
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        Point start = new Point(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken()));
        Point end = new Point(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken()));
        int n = Integer.parseInt(br.readLine());
        ArrayList<Circle> circles = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            st = new StringTokenizer(br.readLine());
            Point center = new Point(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken()));
            double radius = Double.parseDouble(st.nextToken());
            circles.add(new Circle(center, radius));
        }
        double ans = solve(circles, start, end);
        if (ans == 0) {
            pw.println(0);
        } else {
            pw.println(2 * ans / dist(start, end));
        }
        pw.close();
    }
}
