        fac[0] = 1;
        for (int i = 1; i <= MAXN; i++) {
            fac[i] = (fac[i - 1] * i) % MOD;
        }

        long ans = 0;
        for (int i = 1; i <= k; i++) {
            long num = fac[n / 2];
            if (n % 2 == 1) {
                num = (num * (k - i + 1)) % MOD;
            }
            long den = fac[(n - 1) / 2];
            if (n % 2 == 1) {
                den = (den * i) % MOD;
            }
            long cur = (num * pow(fac[(n - 1) / 2], MOD - 2, MOD)) % MOD;
            cur = (cur * pow(fac[(n - 1) / 2], MOD - 2, MOD)) % MOD;
            cur = (cur * fac[n / 2]) % MOD;
            if (n % 2 == 1) {
                cur = (cur * i) % MOD;
            }
            ans = (ans + cur) % MOD;
        }
        System.out.println(ans);
    }

    static long pow(long a, long b, long mod) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = (res * a) % mod;
            }
            a = (a * a) % mod;
            b >>= 1;
        }
        return res;
