typedef long long ll;

const double PI = acos(-1.0);

struct Point {
    double x, y;
};

double dist(const Point &a, const Point &b) {
    return sqrt((a.x - b.x) * (a.x - b.x) + (a.y - b.y) * (a.y - b.y));
}

double area(const Point &a, const Point &b, const Point &c) {
    return abs((a.x * (b.y - c.y) + b.x * (c.y - a.y) + c.x * (a.y - b.y)) / 2.0);
}

double height(const Point &a, const Point &b, const Point &c) {
    double base = dist(a, b);
    double h = 2 * area(a, b, c) / base;
    return h;
}

double radius(const Point &a, const Point &b, const Point &c) {
    double h1 = height(a, b, c);
    double h2 = height(a, c, b);
    double h3 = height(b, c, a);
    double h = min({h1, h2, h3});
    double r = h / 2;
    return r;
}

int main() {
    Point a, b, c;
    cin >> a.x >> a.y >> b.x >> b.y >> c.x >> c.y;
    double r = radius(a, b, c);
    cout << fixed << setprecision(10) << r << endl;
