#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 15

typedef struct {
    int x, y, a;
} City;

int compare(const void *a, const void *b) {
    City *c1 = (City *)a;
    City *c2 = (City *)b;
    return (c1->a - c2->a);
}

double dist(City *c1, City *c2) {
    return sqrt(pow(c1->x - c2->x, 2) + pow(c1->y - c2->y, 2));
}

double solve(City *c, int n) {
    qsort(c, n, sizeof(City), compare);
    double min = c[0].a;
    double max = c[n - 1].a;
    double mid = (min + max) / 2;
    double sum = 0;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            double d = dist(&c[i], &c[j]);
            if (c[i].a - d >= mid) {
                sum += c[i].a - d;
            }
        }
    }
    if (sum >= mid * (n - 1)) {
        return mid;
    } else {
        return solve(c, n, mid + 1);
    }
}

int main() {
    int n;
    scanf("%d", &n);
    City *c = (City *)malloc(n * sizeof(City));
    for (int i = 0; i < n; i++) {
        scanf("%d %d %d", &c[i].x, &c[i].y, &c[i].a);
    }
    printf("%.10lf\n", solve(c, n));
    free(c);
    return 0;
}
