    int n;
    cin >> n;
    vector<ll> x(n), y(n), a(n);
    for (int i = 0; i < n; i++) {
        cin >> x[i] >> y[i] >> a[i];
    }
    cout << fixed << setprecision(15) << solve(x, y, a) << endl;
