            this.y = y;
            this.a = a;
        }
    }

    static class Pair implements Comparable<Pair> {
        long first, second;

        public Pair(long first, long second) {
            this.first = first;
            this.second = second;
        }

        @Override
        public int compareTo(Pair o) {
            return Long.compare(first, o.first);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int n = Integer.parseInt(br.readLine());
        Point[] p = new Point[n];
        for (int i = 0; i < n; i++) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            p[i] = new Point(Long.parseLong(st.nextToken()), Long.parseLong(st.nextToken()), Long.parseLong(st.nextToken()));
        }
        long[] dist = new long[n * n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (i != j) {
                    long dx = p[i].x - p[j].x;
                    long dy = p[i].y - p[j].y;
                    dist[i * n + j] = dx * dx + dy * dy;
                }
            }
        }
        long[] dp = new long[n];
        for (int i = 0; i < n; i++) {
            dp[i] = p[i].a;
        }
        Arrays.sort(dp);
        long ans = dp[n - 1];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (i != j) {
                    long d = dist[i * n + j];
                    long min = Math.min(dp[i], dp[j]);
                    long max = Math.max(dp[i], dp[j]);
                    long val = Math.max(0, min - d);
                    dp[j] = Math.max(dp[j], val);
                    ans = Math.max(ans, Math.min(dp[i], dp[j]));
                }
            }
        }
        System.out.println(ans);
    }
}
