#define MOD 1000000007
#define N 310

long long dp[N][N][N][2];
long long c[N][N];
long long f[N][N][2];
long long g[N][N][2];
long long h[N][N][2];

void add(long long &a, long long b) {
    a = (a + b) % MOD;
}

int main() {
    int n;
    cin >> n;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            cin >> c[i][j];
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            f[i][j][0] = f[i][j][1] = 1;
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            for (int k = 1; k <= n; k++) {
                g[i][j][0] = (g[i][j][0] + f[i][k][0] * f[k][j][0]) % MOD;
                g[i][j][1] = (g[i][j][1] + f[i][k][1] * f[k][j][1]) % MOD;
                h[i][j][0] = (h[i][j][0] + f[i][k][0] * f[k][j][1]) % MOD;
                h[i][j][1] = (h[i][j][1] + f[i][k][1] * f[k][j][0]) % MOD;
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            for (int k = 1; k <= n; k++) {
                for (int l = 0; l < 2; l++) {
                    for (int m = 0; m < 2; m++) {
                        dp[i][j][k][l] = (dp[i][j][k][l] + g[i][k][l] * h[j][k][m] * g[i][j][m]) % MOD;
                    }
                }
            }
        }
    }
    long long ans = 0;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            for (int k = 1; k <= n; k++) {
                for (int l = 0; l < 2; l++) {
                    for (int m = 0; m < 2; m++) {
                        if (c[i][j] == (l ^ m)) {
                            add(ans, dp[i][j][k][l]);
                        }
                    }
                }
            }
        }
    }
    cout << ans << endl;
    return 0;
}
