    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            cin >> c[i][j];
        }
    }

    vector<vector<int>> dp(n + 1, vector<int>(n + 1));
    vector<vector<int>> sum(n + 1, vector<int>(n + 1));
    vector<vector<int>> sum2(n + 1, vector<int>(n + 1));

    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            sum[i][j] = (sum[i - 1][j] + sum[i][j - 1] - sum[i - 1][j - 1] + c[i - 1][j - 1]) % MOD;
            sum2[i][j] = (sum2[i - 1][j] + sum2[i][j - 1] - sum2[i - 1][j - 1] + (i - 1) * (j - 1) % MOD) % MOD;
        }
    }

    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            for (int k = 0; k <= n; k++) {
                for (int l = 0; l <= n; l++) {
                    if (i + k <= n && j + l <= n) {
                        dp[i + k][j + l] = (dp[i + k][j + l] + (long long)sum[i + k - 1][j + l - 1] * sum2[i + k - 1][j + l - 1] % MOD * (1 - (i == 0 || j == 0) + 1) % MOD * (1 - (k == 0 || l == 0) + 1) % MOD) % MOD;
                    }
                }
            }
        }
    }

    int ans = 0;
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            ans = (ans + dp[i][j]) % MOD;
        }
    }

    cout << (1LL * (1 << n) * (1 << n) % MOD * ans % MOD + MOD) % MOD << endl;

    return 0;
}
