const int MOD = 1e9 + 7;

long long modpow(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

long long inv(long long a) {
    return modpow(a, MOD - 2);
}

long long nCr(long long n, long long r) {
    if (r > n) return 0;
    long long res = 1;
    for (long long i = 1; i <= r; i++) {
        res = res * (n - i + 1) % MOD * inv(i) % MOD;
    }
    return res;
}

