    }
    long long ans = 0;
    for (int i = 0; i <= B; i++) {
        int j = B - i;
        int k = C - j;
        if (A + i + j + k != N) continue;
        long long coef = nCr(B, i) * nCr(A + i, A) % MOD * nCr(N - A - B - i, k) % MOD;
        ans = (ans + coef * fac[A + i + j + k]) % MOD;
    }
