import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 5005;

    static long[][] C = new long[MAXN][MAXN];
    static long[] fac = new long[MAXN];
    static long[] inv = new long[MAXN];

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) res = res * a % MOD;
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }

    static long invF(long x) {
        return pow(x, MOD - 2);
    }

    static void init() {
        fac[0] = 1;
        for (int i = 1; i < MAXN; i++) fac[i] = fac[i - 1] * i % MOD;
        inv[MAXN - 1] = invF(fac[MAXN - 1]);
        for (int i = MAXN - 2; i >= 0; i--) inv[i] = inv[i + 1] * (i + 1) % MOD;
        for (int i = 0; i < MAXN; i++) {
            C[i][0] = 1;
            for (int j = 1; j <= i; j++) C[i][j] = (C[i - 1][j - 1] + C[i - 1][j]) % MOD;
        }
    }

    static long choose(int n, int k) {
        return C[n][k] * fac[k] % MOD * inv[n - k] % MOD;
    }

    public static void main(String[] args) throws IOException {
        init();
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int a = Integer.parseInt(st.nextToken());
        int b = Integer.parseInt(st.nextToken());
        int c = Integer.parseInt(st.nextToken());
        long ans = 1;
        for (int i = 0; i <= a; i++) {
            for (int j = 0; j <= b; j++) {
                for (int k = 0; k <= c; k++) {
                    if (i + j + k == a + b + c) {
                        long tmp = choose(a, i) * choose(b, j) % MOD * choose(c, k) % MOD;
                        tmp = tmp * choose(n - a - b - c, a - i) % MOD;
                        ans = (ans + tmp) % MOD;
                    }
                }
            }
        }
        System.out.println(ans);
    }
}
