typedef struct {
    double x, y;
} Point;

double distance(Point p1, Point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

double area(Point p1, Point p2, Point p3) {
    return abs((p1.x * (p2.y - p3.y) + p2.x * (p3.y - p1.y) + p3.x * (p1.y - p2.y)) / 2.0);
}

double height(Point p1, Point p2, Point p3) {
    double base = distance(p1, p2);
    double h = (2 * area(p1, p2, p3)) / base;
    return h;
}

double radius(Point p1, Point p2, Point p3) {
    double h = height(p1, p2, p3);
    double a = distance(p1, p2);
    double b = distance(p2, p3);
    double c = distance(p3, p1);
    double s = (a + b + c) / 2.0;
    double r = (h * (s - a) * (s - b) * (s - c)) / (s * (s - a + b) * (s - a + c) * (s - b + c));
    return r;
}

int main() {
