using namespace std;

double cross(const double a[2], const double b[2]) {
    return a[0] * b[1] - a[1] * b[0];
}

double dot(const double a[2], const double b[2]) {
    return a[0] * b[0] + a[1] * b[1];
}

double dist(const double a[2], const double b[2]) {
    return sqrt((a[0] - b[0]) * (a[0] - b[0]) + (a[1] - b[1]) * (a[1] - b[1]));
}

double area(const double a[2], const double b[2], const double c[2]) {
    return abs(cross(a, b) + cross(b, c) + cross(c, a)) / 2;
}

double height(const double a[2], const double b[2], const double c[2]) {
    double s = area(a, b, c) * 2;
    double h = (s * 2) / (dist(a, b) + dist(b, c) + dist(c, a));
    return h;
}

double solve() {
    double x[3], y[3];
    for (int i = 0; i < 3; i++) {
        cin >> x[i] >> y[i];
    }
    double h = height(x, y, x + 2);
    double r = h / 3;
    double r2 = (dist(x, y) + dist(x + 2, y + 2) - dist(x, y + 2)) / 6;
    return min(r, r2);
}

