#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 15

typedef struct {
    int x, y, a;
} City;

int compare(const void *a, const void *b) {
    City *c1 = (City *)a;
    City *c2 = (City *)b;
    return (c1->a - c2->a);
}

double dist(City *c1, City *c2) {
    return sqrt(pow(c1->x - c2->x, 2) + pow(c1->y - c2->y, 2));
}

double solve(City *cities, int n) {
    double min = 0;
    for (int i = 0; i < n; i++) {
        min = fmin(min, cities[i].a);
    }
    double max = 1e9;
    while (max - min > 1e-9) {
        double mid = (max + min) / 2;
        double sum = 0;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                double d = dist(&cities[i], &cities[j]);
                if (cities[i].a - d > 0) {
                    sum += fmin(cities[i].a - d, mid);
                }
            }
        }
        if (sum >= mid * n) {
            min = mid;
        } else {
            max = mid;
        }
    }
    return min;
}

int main() {
    int n;
    scanf("%d", &n);
    City cities[n];
    for (int i = 0; i < n; i++) {
        scanf("%d %d %d", &cities[i].x, &cities[i].y, &cities[i].a);
    }
    qsort(cities, n, sizeof(City), compare);
    double ans = solve(cities, n);
    printf("%.9lf\n", ans);
    return 0;
}
