public class Main {
    static class Point {
        long x, y, a;

        public Point(long x, long y, long a) {
            this.x = x;
            this.y = y;
            this.a = a;
        }
    }

    static class Pair implements Comparable<Pair> {
        long first, second;

        public Pair(long first, long second) {
            this.first = first;
            this.second = second;
        }

        @Override
        public int compareTo(Pair o) {
            return Long.compare(first, o.first);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int n = Integer.parseInt(br.readLine());
        Point[] p = new Point[n + 1];
        for (int i = 1; i <= n; i++) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            p[i] = new Point(Long.parseLong(st.nextToken()), Long.parseLong(st.nextToken()), Long.parseLong(st.nextToken()));
        }
        long ans = 0;
        for (int i = 1; i <= (1 << n); i++) {
            long sum = 0;
            for (int j = 1; j <= n; j++) {
                if ((i & (1 << (j - 1))) != 0) {
                    sum += p[j].a;
                }
            }
            long[] dist = new long[n + 1];
            for (int j = 1; j <= n; j++) {
                dist[j] = (long) Math.sqrt(Math.pow(p[j].x, 2) + Math.pow(p[j].y, 2));
            }
            Pair[] pair = new Pair[n + 1];
            for (int j = 1; j <= n; j++) {
                pair[j] = new Pair(p[j].a - dist[j], p[j].a);
            }
            Arrays.sort(pair, 1, n + 1);
            for (int j = 1; j <= n; j++) {
                if ((i & (1 << (j - 1))) == 0) {
                    sum -= pair[j].first;
                    sum += Math.min(pair[j].second, pair[j].first + dist[j]);
                }
            }
            ans = Math.max(ans, sum / n);
        }
        pw.println(ans);
        pw.close();
    }
}
