int cmp(const void *a, const void *b) {
    return (*(int *)a - *(int *)b);
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &l[i], &r[i]);
    }
    qsort(l, n, sizeof(int), cmp);
    qsort(r, n, sizeof(int), cmp);

    int dp[n + 1][n + 1];
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            dp[i][j] = INF;
        }
    }
    dp[0][0] = 0;

    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= i; j++) {
            dp[i + 1][j + 1] = min(dp[i + 1][j + 1], dp[i][j] + abs(l[i] - r[j]));
            dp[i + 1][j] = min(dp[i + 1][j], dp[i][j] + abs(r[i] - l[j]));
        }
    }

    int ans = INF;
    for (int i = 0; i <= n; i++) {
        ans = min(ans, dp[n][i]);
    }

    printf("%d\n", ans);
    return 0;
}

int min(int a, int b) {
    return a < b ? a : b;
