long long f[N][N][2];
long long g[N][N][2];
long long h[N][N][2];

void add(long long &a, long long b) {
    a = (a + b) % MOD;
}

int main() {
    int n;
    cin >> n;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            cin >> c[i][j];
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            f[i][j][0] = 1;
            f[i][j][1] = 1;
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            for (int k = 0; k < n; k++) {
                g[i][j][0] = (g[i][j][0] + f[i][k][0] * f[k][j][0]) % MOD;
                g[i][j][1] = (g[i][j][1] + f[i][k][1] * f[k][j][1]) % MOD;
            }
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            for (int k = 0; k < n; k++) {
                h[i][j][0] = (h[i][j][0] + g[i][k][0] * g[k][j][0]) % MOD;
                h[i][j][1] = (h[i][j][1] + g[i][k][1] * g[k][j][1]) % MOD;
            }
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            for (int k = 0; k < n; k++) {
                for (int l = 0; l < n; l++) {
                    for (int m = 0; m < n; m++) {
                        for (int p = 0; p < 2; p++) {
                            for (int q = 0; q < 2; q++) {
                                for (int r = 0; r < 2; r++) {
                                    for (int s = 0; s < 2; s++) {
                                        dp[i][j][k][p] = (dp[i][j][k][p] + h[i][l][q] * h[j][m][r] * h[k][l][s] * (c[l][m] == 1 ? 1 : (c[l][m] == 0 ? 0 : 1))) % MOD;
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
    long long ans = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            for (int k = 0; k < n; k++) {
                for (int p = 0; p < 2; p++) {
                    add(ans, dp[i][j][k][p]);
                }
            }
        }
    }
    cout << ans << endl;
    return 0;
}
