        for (int j = 0; j <= i; j++) {
            for (int k = 0; k <= i; k++) {
                if (c[i][j] == 1) {
                    dp[i + 1][k + 1] = (dp[i + 1][k + 1] + dp[i][j]) % MOD;
                    dp[i + 1][k] = (dp[i + 1][k] + dp[i][j]) % MOD;
                } else {
                    dp[i + 1][k] = (dp[i + 1][k] + dp[i][j]) % MOD;
                }
            }
        }
    }

    int ans = 0;
    for (int i = 0; i <= n; i++) {
        ans = (ans + dp[n][i] * dp[n][i]) % MOD;
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (c[i][j] == 0) {
                ans = (ans + (dp[n][i] * dp[n][j]) % MOD) % MOD;
            }
        }
    }

    cout << ans << endl;

    return 0;
}
