#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

typedef long long ll;

ll ncr(ll n, ll r) {
    ll res = 1;
    for (ll i = 1; i <= r; i++) {
        res = (res * (n - i + 1)) % MOD;
        res = (res * pow(i, MOD - 2)) % MOD;
    }
    return res;
}

ll pow(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b & 1) res = (res * a) % MOD;
        a = (a * a) % MOD;
        b >>= 1;
    }
    return res;
}

ll solve(ll n, ll a, ll b, ll c) {
    if (a + b + c != n) return 0;
    ll res = 1;
    for (ll i = 0; i <= b; i++) {
        for (ll j = 0; j <= c; j++) {
            ll k = n - a - i - j;
            if (k < 0 || k > c) continue;
            res = (res + (ncr(b, i) * ncr(c, j) * ncr(n - a - i - j, k)) % MOD) % MOD;
        }
    }
    return res;
}

int main() {
    ll n, a, b, c;
    scanf("%lld %lld %lld %lld", &n, &a, &b, &c);
    printf("%lld\n", solve(n, a, b, c));
    return 0;
}
