#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

int main() {
    int N, A, B, C;
    cin >> N >> A >> B >> C;

    vector<int> fac(N + 1);
    fac[0] = 1;
    for (int i = 1; i <= N; i++) {
        fac[i] = (1LL * fac[i - 1] * i) % MOD;
    }

    vector<int> inv(N + 1);
    inv[N] = pow(fac[N], MOD - 2, MOD);
    for (int i = N - 1; i >= 0; i--) {
        inv[i] = (1LL * inv[i + 1] * (i + 1)) % MOD;
    }

    auto Cnk = [&](int n, int k) {
        if (n < k) return 0;
        return (1LL * fac[n] * inv[k] % MOD * inv[n - k]) % MOD;
    };

    vector<vector<int>> dp(N + 1, vector<int>(N + 1, 0));
    dp[0][0] = 1;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j <= N; j++) {
            if (dp[i][j] == 0) continue;
            for (int k = 1; k <= 3; k++) {
                if (j + k > N) break;
                dp[i + 1][j + k] = (dp[i + 1][j + k] + 1LL * dp[i][j] * Cnk(i + j, j)) % MOD;
            }
        }
    }

    int ans = dp[N][N];
    for (int i = 0; i <= N; i++) {
        for (int j = 0; j <= N; j++) {
            for (int k = 0; k <= N; k++) {
                if (i + j + k != N) continue;
                int a = i, b = j, c = k;
                if (a > b) swap(a, b);
                if (b > c) swap(b, c);
                if (a > b) swap(a, b);
                ans = (ans + 1LL * dp[N][N] * Cnk(N - a - b - c, N - a - b) * Cnk(a, a + b - c)) % MOD;
            }
        }
    }

    cout << ans << endl;

    return 0;
}
