def factorial(n):
    f = [1] * (n + 1)
    for i in range(1, n + 1):
        f[i] = f[i - 1] * i % MOD
    return f

def combination(n, k, f):
    return f[n] * pow(f[n - k], MOD - 2, MOD) % MOD

def solve():
    n, a, b, c = map(int, input().split())
    f = factorial(n)
    ans = 1
    for i in range(a + 1):
        for j in range(b + 1):
            for k in range(c + 1):
                if i + 2 * j + 3 * k == n:
                    ans += combination(n - i - 2 * j - 3 * k, 2 * j + 3 * k, f)
    print(ans % MOD)

t = 1
for _ in range(t):
