bool isMirrorString(const std::string& s) {
    std::string t = s;
    std::reverse(t.begin(), t.end());
    for (size_t i = 0; i < s.size(); ++i) {
        if (s[i] == 'b') t[i] = 'd';
        else if (s[i] == 'd') t[i] = 'b';
        else if (s[i] == 'p') t[i] = 'q';
        else if (s[i] == 'q') t[i] = 'p';
    }
    return t == s;
}

int main() {
    std::string s;
    std::cin >> s;
    std::cout << (isMirrorString(s) ? "Yes" : "No") << std::endl;
    return 0;
}
