public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        int n = sc.nextInt();
        int q = sc.nextInt();
        int[] arr = new int[n + 1];
        int[] pos = new int[n + 1];
        for (int i = 0; i < q; i++) {
            int a = sc.nextInt();
            int b = sc.nextInt();
            arr[a] = 1;
            arr[b] = 1;
            pos[a] = b;
        }
        int[] dp = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            if (arr[i] == 1) {
                dp[i] = dp[i - 1] + 1;
            } else {
                dp[i] = dp[i - 1];
            }
        }
        int ans = 0;
        for (int i = 1; i <= n; i++) {
            if (arr[i] == 1) {
                int l = i - dp[i];
                int r = n - i + dp[i];
                if (l > 1 && r > 1) {
                    ans += 2;
                } else if (l > 1 || r > 1) {
                    ans += 1;
                }
            }
        }
        if (pos[n] == n) {
            ans += 2;
        } else if (pos[n] != 0) {
            ans += 1;
        }
