```c
#include <stdio.h>
#include <stdlib.h>

#define N 100005

int n, m, cnt, ans;
int head[N], to[N << 1], nxt[N << 1], vis[N], dep[N];
int fa[N][20];

void add(int u, int v) {
    to[m] = v;
    nxt[m] = head[u];
    head[u] = m++;
}

void dfs(int u, int f) {
    fa[u][0] = f;
    dep[u] = dep[f] + 1;
    for (int i = 1; i <= 18; i++) {
        fa[u][i] = fa[fa[u][i - 1]][i - 1];
    }
    for (int i = head[u]; i != -1; i = nxt[i]) {
        int v = to[i];
        if (v == f) continue;
        dfs(v, u);
    }
}

int lca(int u, int v) {
    if (dep[u] < dep[v]) {
        int t = u;
        u = v;
        v = t;
    }
    for (int i = 18; i >= 0; i--) {
        if (dep[fa[u][i]] >= dep[v]) {
            u = fa[u][i];
        }
    }
    if (u == v) return u;
    for (int i = 18; i >= 0; i--) {
        if (fa[u][i] != fa[v][i]) {
            u = fa[u][i];
            v = fa[v][i];
        }
    }
    return fa[u][0];
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i < n; i++) {
        int u, v;
        scanf("%d%d", &u, &v);
        add(u, v);
        add(v, u);
    }
    dfs(1, 0);
    for (int i = 1; i <= n; i++) {
        vis[i] = 0;
    }
    for (int i = 1; i <= n; i++) {
        for (int j = head[i]; j != -1; j = nxt[j]) {
            int v = to[j];
            if (vis[i] && vis[v]) {
                ans = max(ans, dep[i] + dep[v] - 2 * dep[lca(i, v)] + 1);
            }
            vis[v] = 1;
        }
    }
    printf("%d\n", ans);
    return 0;
}
