import java.io.*;
import java.util.*;

public class Main {
    static int N = (int) 1e5 + 5;
    static int[] head = new int[N];
    static int[] to = new int[N * 2];
    static int[] nxt = new int[N * 2];
    static int tot = 1;
    static int[] dep = new int[N];
    static int[] son = new int[N];
    static int[] siz = new int[N];
    static int[] top = new int[N];
    static int[] dfn = new int[N];
    static int[] low = new int[N];
    static int[] id = new int[N];
    static int[] st = new int[N];
    static int[] fa = new int[N];
    static int[] dfn_cnt = new int[1];
    static int[] id_cnt = new int[1];
    static int[] ans = new int[1];
    static int[] cut = new int[N];
    static int[] dfn_son = new int[N];
    static int[] dfn_cnt_son = new int[N];

    static void add(int u, int v) {
        nxt[++tot] = head[u];
        to[head[u] = tot] = v;
    }

    static void dfs1(int u, int f) {
        dep[u] = dep[f] + 1;
        fa[u] = f;
        siz[u] = 1;
        for (int i = head[u]; i != 0; i = nxt[i]) {
            int v = to[i];
            if (v == f) continue;
            dfs1(v, u);
            siz[u] += siz[v];
            if (siz[v] > siz[son[u]]) son[u] = v;
        }
    }

    static void dfs2(int u, int t) {
        top[u] = t;
        st[++dfn_cnt[0]] = u;
        id[u] = ++id_cnt[0];
        dfn[u] = ++dfn_cnt[0];
        low[u] = dfn[u];
        if (son[u] != 0) {
            dfs2(son[u], t);
            for (int i = head[u]; i != 0; i = nxt[i]) {
                int v = to[i];
                if (v == fa[u] || v == son[u]) continue;
                dfs2(v, v);
            }
        }
        while (st[dfn_cnt[0]] != u) {
            int v = st[dfn_cnt[0]--];
            cut[id[v]] = u;
            dfn[v] = dfn_cnt[0] + 1;
            low[v] = dfn_cnt[0] + 1;
        }
        st[dfn_cnt[0]--] = u;
    }

    static void dfs3(int u, int f) {
        for (int i = head[u]; i != 0; i = nxt[i]) {
            int v = to[i];
            if (v == f) continue;
            dfn_son[v] = dfn[u];
            dfn_cnt_son[v] = 1;
            dfs3(v, u);
        }
    }

    static void dfs4(int u, int f) {
        for (int i = head[u]; i != 0; i = nxt[i]) {
            int v = to[i];
            if (v == f) continue;
            ans[0] = Math.max(ans[0], dfn_cnt_son[v] + dfs4(v, u));
            dfs4(v, u);
        }
        return ans[0];
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int n = Integer.parseInt(br.readLine());
        for (int i = 1; i < n; i++) {
            String[] s = br.readLine().split(" ");
            int u = Integer.parseInt(s[0]);
            int v = Integer.parseInt(s[1]);
            add(u, v);
            add(v, u);
        }
        dfs1(1, 0);
        dfs2(1, 1);
        dfs3(1, 0);
        for (int i = 1; i <= n; i++) {
            if (cut[i] != 0) {
                dfn_son[i] = dfn[cut[i]];
                dfn_cnt_son[i] = 1;
                dfs3(i, cut[i]);
            }
        }
        dfs4(1, 0);
        System.out.println(ans[0]);
    }
}
