#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define N 1000
#define M 1000

int compare(const void *a, const void *b) {
    int *x = (int *)a;
    int *y = (int *)b;
    int i;

    for (i = 0; i < M; i++) {
        if (x[i] != y[i]) {
            return x[i] - y[i];
        }
    }

    return 0;
}

int main() {
    int n, m, i, j, k, count, ans;
    int a[N][M];

    scanf("%d %d", &n, &m);
    for (i = 0; i < n; i++) {
        for (j = 0; j < m; j++) {
            scanf("%d", &a[i][j]);
        }
    }

    qsort(a, n, m * sizeof(int), compare);

    ans = 0;
    for (i = 1; i < n; i++) {
        count = 0;
        for (j = 0; j < m; j++) {
            if (a[i][j] > a[i - 1][j]) {
                count++;
            } else if (a[i][j] < a[i - 1][j]) {
                break;
            }
        }
        if (count == m) {
            ans++;
        } else {
            break;
        }
    }

    if (ans == n - 1) {
        printf("%d\n", n - 1);
    } else {
        printf("-1\n");
    }

    return 0;
}
