        int[][] a = new int[n][m];
        for (int i = 0; i < n; i++) {
            st = new StringTokenizer(br.readLine());
            for (int j = 0; j < m; j++) {
                a[i][j] = Integer.parseInt(st.nextToken());
            }
        }
        int[] prefix = new int[n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                prefix[i] += a[i][j];
            }
        }
        Arrays.sort(prefix);
        int ans = 0;
        for (int i = 0; i < n; i++) {
            int j = Arrays.binarySearch(prefix, a[i][0]);
            if (j < 0) {
                j = -j - 1;
            }
            if (j < i) {
                ans = -1;
                break;
            }
            for (int k = 0; k < m; k++) {
                a[i][k] += prefix[j] - prefix[i];
            }
            prefix[j] = a[i][0];
            Arrays.sort(prefix);
            ans++;
        }
        System.out.println(ans);
    }
}
