import sys
input = sys.stdin.readline

class LCA:
    def __init__(self, n, G, root):
        self.N = n
        self.G = G
        self.log = [0] * (n + 1)
        self.log[1] = 0
        for i in range(2, n + 1):
            self.log[i] = self.log[i >> 1] + 1
        self.par = [[-1] * (self.log[n] + 1) for _ in range(n)]
        self.depth = [-1] * n
        self.depth[root] = 0
        self.par[root][0] = root
        for i in range(self.log[n] + 1):
            for v in range(n):
                if self.par[v][i] == -1:
                    self.par[v][i + 1] = -1
                else:
                    self.par[v][i + 1] = self.par[self.par[v][i]][i]
        self.path = [[0] * (self.log[n] + 1) for _ in range(n)]
        self.path[root][0] = root
        for i in range(self.log[n] + 1):
            for v in range(n):
                if self.par[v][i] == -1:
                    self.path[v][i + 1] = -1
                else:
                    self.path[v][i + 1] = self.path[v][i] if self.path[v][i] != root else self.par[v][i]
        self.up = [[-1] * (self.log[n] + 1) for _ in range(n)]
        self.up[root][0] = root
        for i in range(self.log[n] + 1):
            for v in range(n):
                if self.up[v][i] == -1:
                    self.up[v][i + 1] = -1
                else:
                    self.up[v][i + 1] = self.up[self.up[v][i]][i]
        self.time = 0
        self.t = [-1] * n
        self.t[root] = 0
        self.time += 1
        self.q = [root]
        while self.q:
            v = self.q.pop()
            for u in G[v]:
                if self.t[u] == -1:
                    self.t[u] = self.time
                    self.depth[u] = self.depth[v] + 1
                    self.par[u][0] = v
                    self.path[u][0] = v
                    self.up[u][0] = v
                    self.q.append(u)
                    self.time += 1
                    for i in range(1, self.log[n] + 1):
                        if self.par[u][i - 1] != -1:
                            self.par[u][i] = self.par[self.par[u][i - 1]][i - 1]
                            self.path[u][i] = self.path[u][i - 1] if self.path[u][i - 1] != -1 else self.par[u][i]
                            self.up[u][i] = self.up[u][i - 1] if self.up[u][i - 1] != -1 else self.par[u][i]

    def lca(self, u, v):
        if self.depth[u] < self.depth[v]:
            u, v = v, u
        for i in range(self.log[self.N] + 1):
            if self.depth[self.par[u][i]] >= self.depth[v]:
                u = self.par[u][i]
        if u == v:
            return u
        for i in range(self.log[self.N] + 1):
            if self.par[u][i] != self.par[v][i]:
                u = self.par[u][i]
                v = self.par[v][i]
        return self.par[u][0]

    def path_lca(self, u, v):
        if self.depth[u] < self.depth[v]:
            u, v = v, u
        for i in range(self.log[self.N] + 1):
            if self.depth[self.par[u][i]] >= self.depth[v]:
                u = self.par[u][i]
        if u == v:
            return u
        for i in range(self.log[self.N] + 1):
            if self.par[u][i] != self.par[v][i]:
                u = self.par[u][i]
                v = self.par[v][i]
        return self.par[u][0]

    def up_path(self, v, k):
        for i in range(self.log[self.N] + 1):
            if k & (1 << i):
                v = self.up[v][i]
        return v

    def dist(self, u, v):
        return self.depth[u] + self.depth[v] - 2 * self.depth[self.lca(u, v)]

    def path_dist(self, u, v):
        return self.depth[u] + self.depth[v] - 2 * self.depth[self.path_lca(u, v)]

def main():
    N, M = map(int, input().split())
    G = [[] for _ in range(N)]
    for _ in range(M):
        a, b, c = map(int, input().split())
        a -= 1
        b -= 1
        G[a].append((b, c))
        G[b].append((a, c))
    Q = int(input())
    S = []
    T = []
    for _ in range(Q):
        s, t = map(int, input().split())
        s -= 1
        t -= 1
        S.append(s)
        T.append(t)
    lca = LCA(N, G, 0)
    ans = [0] * Q
    for i in range(Q):
        s = S[i]
        t = T[i]
        if lca.depth[s] < lca.depth[t]:
            s, t = t, s
        u = lca.up_path(s, lca.depth[s] - lca.depth[t])
        v = lca.path_lca(s, t)
        ans[i] = lca.path_dist(u, v)
        for e in lca.G[u]:
            if e[0] == v:
                ans[i] -= e[1]
    print('\n'.join(map(str, ans)))

main()
