        return x == fa[x] ? x : (fa[x] = find(fa[x]));
    }

    static void dfs(int u, int f) {
        dep[u] = dep[fa[u] = f] + 1;
        siz[u] = 1;
        for (int i = head[u]; i != 0; i = nxt[i]) {
            int v = to[i];
            if (v == f) continue;
            dfs(v, u);
            siz[u] += siz[v];
        }
    }

    static void dfs2(int u, int f) {
