    static class Pair implements Comparable<Pair> {
        int first;
        int second;

        public Pair(int first, int second) {
            this.first = first;
            this.second = second;
        }

        public int compareTo(Pair other) {
            return Integer.compare(this.first, other.first);
        }
    }

    public static void main(String[] args) throws IOException {
        FastReader reader = new FastReader();
        int n = reader.nextInt();
        int[] a = new int[n + 1];
        int[] b = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            a[i] = reader.nextInt();
            b[i] = reader.nextInt();
        }
        int[] f = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            f[i] = Math.max(a[i], b[i]);
        }
        int[] pre = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            pre[i] = pre[i - 1] + (f[i] != f[i - 1] ? 1 : 0);
        }
        int ans = 0;
        for (int i = 1; i <= n; i++) {
            int x = a[i], y = b[i];
            if (x == y) {
                if (x != 0) {
                    System.out.println(-1);
                    return;
                }
                continue;
            }
            if (x > y) {
                int temp = x;
                x = y;
                y = temp;
            }
            int l = 0, r = n;
            while (l < r) {
                int mid = (l + r) >> 1;
                if (pre[mid] >= x) {
                    r = mid;
                } else {
                    l = mid + 1;
                }
            }
            if (l == n || f[l + 1] != y) {
                System.out.println(-1);
                return;
            }
            ans += (l - x + 1) + (y - f[x]);
        }
        System.out.println(ans);
    }
