                        long long x = k;
                        while (x > 0) {
                            if (x % 2 == 1) {
                                long long y = a[j] + (1LL << k);
                                if (y > max) {
                                    max = y;
                                }
                            }
                            x /= 2;
                        }
                        dp[k * 2] = dp[k] * (1LL << (a[j] % 64));
                    }
                }
            }
        }
        for (int j = 0; j < 64; j++) {
            if (dp[j] > 0) {
                if (max == (1LL << j)) {
                    dp[j] = -1;
                }
            }
        }
    }

    long long ans = 0;
    for (int i = 0; i < 64; i++) {
        if (dp[i] > 0) {
            ans += dp[i];
        }
    }

    printf("%lld\n", ans);

    return 0;
}
