    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st;

        n = Integer.parseInt(br.readLine());
        a = new int[n + 1];
        b = new int[n + 1];
        f = new int[n + 1];
        d = new int[n + 1];

        for (int i = 1; i <= n; i++) {
            st = new StringTokenizer(br.readLine());
            a[i] = Integer.parseInt(st.nextToken());
            b[i] = Integer.parseInt(st.nextToken());
            d[i] = Math.max(a[i], b[i]);
        }

        Arrays.sort(d, 1, n + 1);

        for (int i = 1; i <= n; i++) {
            f[i] = f[i - 1] + (d[i] != d[i - 1] ? 1 : 0);
        }

        for (int i = 1; i <= n; i++) {
            int l = 1, r = n;
            while (l < r) {
                int mid = (l + r + 1) / 2;
                if (check(i, mid)) r = mid - 1;
                else l = mid;
            }
            if (check(i, r)) {
                pw.println(r);
                pw.close();
                return;
            }
        }

        pw.println(-1);
        pw.close();
    }

    static boolean check(int x, int y) {
        if (a[x] == b[x]) return true;
        if (a[x] > b[x]) {
            int t = a[x];
            a[x] = b[x];
            b[x] = t;
        }
        if (a[x] + y < b[x]) return false;
        if (a[x] + y == b[x]) return f[y] == y;
        return f[y] == y + 1;
    }
