            int cnt = 0;
            int j = i;
            while (vis[j] == 0) {
                vis[j] = 1;
                cnt++;
                j = adj[j];
            }
            res = (res * pow(2, cnt - 1)) % MOD;
        }
    }

    printf("%d\n", res);

    free(adj);
    free(vis);
    free(dp);
    free(dp2);

    return 0;
}

int pow(int a, int b) {
    int res = 1;
    while (b > 0) {
        if (b & 1) {
            res = (res * a) % MOD;
        }
        a = (a * a) % MOD;
        b >>= 1;
    }
    return res;
}
