#define N 200000
#define Q 200000
#define INF 1000000000000000000LL

typedef struct edge {
    int u, v;
    long long w;
} edge;

edge e[Q * 4];
int n, q, cnt;
long long ans;

void add(int i, int j, int k, int l, int r, int u, int v, long long w) {
    if (i > r || j < l) return;
    if (i >= l && j <= r) {
        e[cnt].u = u;
        e[cnt].v = v;
        e[cnt].w = w;
        cnt++;
        return;
    }
    int mid = (i + j) / 2;
    add(i, mid, k, l, r, u, v, w);
    add(mid + 1, j, k, l, r, u, v, w);
}

int find(int i, int j, int k, int l, int r, int u, int v) {
    if (i > r || j < l) return INF;
    if (i >= l && j <= r) {
        int w = e[k].w;
        if (w < e[u].w) return k;
        return u;
    }
    int mid = (i + j) / 2;
    int t = find(i, mid, k, l, r, u, v);
    if (t != INF) return t;
    return find(mid + 1, j, k, l, r, u, v);
}

int main() {
    scanf("%d%d", &n, &q);
    for (int i = 0; i < q; i++) {
        int a, b;
        long long c;
        scanf("%d%d%lld", &a, &b, &c);
        a %= n;
        b %= n;
        for (int j = 0; j < 4; j++) {
            add(0, n - 1, cnt, (a + j) % n, (a + j + 1) % n, (b + j) % n, (b + j + 1) % n, c + j);
        }
    }
    for (int i = 0; i < n; i++) {
        ans += e[i].w;
    }
    for (int i = 0; i < n; i++) {
        int j = find(0, n - 1, i, i, i, i + 1, i + 2);
        if (j != INF) {
            ans -= e[j].w;
        }
    }
    printf("%lld\n", ans);
    return 0;
}
