#define N 200000
#define M 200000

typedef struct {
    int l, r;
    long long sum;
} SegmentTree;

SegmentTree tree[4 * N + 5];

void init(int n) {
    for (int i = 0; i < 4 * n + 5; i++) {
        tree[i].l = tree[i].r = 0;
        tree[i].sum = 0;
    }
}

void update(int i, int l, int r, int x, long long v) {
    if (l == r) {
        tree[i].sum = v;
        return;
    }
    int mid = (l + r) >> 1;
    if (x <= mid) update(2 * i, l, mid, x, v);
    else update(2 * i + 1, mid + 1, r, x, v);
    tree[i].sum = tree[2 * i].sum + tree[2 * i + 1].sum;
}

long long query(int i, int l, int r, int a, int b) {
    if (l >= a && r <= b) return tree[i].sum;
    int mid = (l + r) >> 1;
    long long res = 0;
    if (a <= mid) res += query(2 * i, l, mid, a, b);
    if (b > mid) res += query(2 * i + 1, mid + 1, r, a, b);
    return res;
}

int main() {
