```java
import java.io.*;
import java.util.*;

public class Main {
    static class SegmentTree {
        int n;
        long[] tree;

        public SegmentTree(int n) {
            this.n = n;
            tree = new long[4 * n];
        }

        public void update(int i, long val) {
            update(1, 0, n - 1, i, val);
        }

        private void update(int node, int start, int end, int i, long val) {
            if (start == end) {
                tree[node] = val;
            } else {
                int mid = (start + end) / 2;
                if (i <= mid) {
                    update(2 * node, start, mid, i, val);
                } else {
                    update(2 * node + 1, mid + 1, end, i, val);
                }
                tree[node] = Math.max(tree[2 * node], tree[2 * node + 1]);
            }
        }

        public long query(int i, int j) {
            return query(1, 0, n - 1, i, j);
        }

        private long query(int node, int start, int end, int i, int j) {
            if (i > end || j < start) {
                return Long.MIN_VALUE;
            } else if (i <= start && j >= end) {
                return tree[node];
            } else {
                int mid = (start + end) / 2;
                return Math.max(query(2 * node, start, mid, i, j), query(2 * node + 1, mid + 1, end, i, j));
            }
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int[] a = new int[n - 1];
        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < n - 1; i++) {
            a[i] = Integer.parseInt(st.nextToken());
        }
        int m = Integer.parseInt(br.readLine());
        int[] x = new int[m];
        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < m; i++) {
            x[i] = Integer.parseInt(st.nextToken());
        }
        long[] ans = new long[m];
        SegmentTree seg = new SegmentTree(n - 1);
        for (int i = 0; i < n - 2; i++) {
            seg.update(i, a[i]);
        }
        long sum = 0;
        for (int i = n - 2; i >= 0; i--) {
            sum += a[i];
            long max = seg.query(i + 1, n - 2);
            if (max < a[i]) {
                break;
            }
            seg.update(i, max);
        }
        for (int i = 0; i < m; i++) {
            long max = seg.query(0, n - 2);
            if (max < x[i]) {
                ans[i] = sum + x[i] - max;
            } else {
                ans[i] = sum + x[i] - (max - a[n - 2]);
            }
        }
        for (long an : ans) {
            pw.println(an);
        }
        pw.close();
    }
}
