        for _ in range(U[i]):
            q.append("U" + str(i + 1))
        for _ in range(D[i]):
            q.append("D" + str(i + 1))

    ans = []
    while q:
        x = q.popleft()
        ans.append(x)
        if x[0] == 'L':
            R[int(x[1]) - 1] -= 1
            if R[int(x[1]) - 1] == 0:
                q.append("R" + str(int(x[1]) - 1))
