```java
import java.io.*;
import java.util.*;

public class Main {
    static class DSU {
        int[] parent;
        int[] size;

        public DSU(int n) {
            parent = new int[n];
            size = new int[n];
            for (int i = 0; i < n; i++) {
                parent[i] = i;
                size[i] = 1;
            }
        }

        public int find(int x) {
            if (parent[x] != x) {
                parent[x] = find(parent[x]);
            }
            return parent[x];
        }

        public void union(int x, int y) {
            int rootX = find(x);
            int rootY = find(y);
            if (rootX != rootY) {
                if (size[rootX] > size[rootY]) {
                    parent[rootY] = rootX;
                    size[rootX] += size[rootY];
                } else {
                    parent[rootX] = rootY;
                    size[rootY] += size[rootX];
                }
            }
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int m = Integer.parseInt(st.nextToken());
        DSU dsu = new DSU(m + 1);
        int[] group = new int[m + 1];
        Arrays.fill(group, -1);
        for (int i = 0; i < n; i++) {
            st = new StringTokenizer(br.readLine());
            int k = Integer.parseInt(st.nextToken());
            for (int j = 0; j < k; j++) {
                int x = Integer.parseInt(st.nextToken());
                if (group[x] != -1) {
                    dsu.union(group[x], i + 1);
                } else {
                    group[x] = i + 1;
                }
            }
        }
        for (int i = 1; i <= m; i++) {
            if (group[i] != -1) {
                dsu.union(group[i], m + 1);
            }
        }
        int root = dsu.find(m + 1);
        boolean flag = true;
        for (int i = 1; i <= n; i++) {
            if (dsu.find(i) != root) {
                flag = false;
                break;
            }
        }
        System.out.println(flag ? "YES" : "NO");
    }
}
