public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int m = scanner.nextInt();
        int[] x = new int[n];
        for (int i = 0; i < n; i++) {
            x[i] = scanner.nextInt();
        }
        Map<Integer, Integer> map = new HashMap<>();
        for (int i = 0; i < n; i++) {
            map.put(x[i], map.getOrDefault(x[i], 0) + 1);
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            int num = x[i];
            ans += map.get(num) / 2;
            map.put(num, map.get(num) % 2);
            int complement = m - num;
            if (complement != num && map.containsKey(complement)) {
                ans += map.get(complement);
                map.put(complement, 0);
            }
        }
        for (int value : map.values()) {
            ans += value / 2;
        }
        System.out.println(ans);
    }
}
