            Arrays.fill(g[i], -1);
        }
        dp = new int[n + 1][n + 1];
        for (int i = 0; i <= n; i++) {
            Arrays.fill(dp[i], -1);
        }
        int ans = dfs(1, 1);
        pw.println(ans);
        pw.close();
    }

    static int dfs(int u, int p) {
        if (u == n) return 1;
        if (dp[u][p] != -1) return dp[u][p];
        int res = 0;
        for (int v = 1; v <= n; v++) {
            if (v == p) continue;
            if (g[u][v] == 1) {
                res = (res + dfs(v, u)) % MOD;
            }
        }
        return dp[u][p] = res;
    }
