    e.push_back({u, v, w});
    st.push_back({w, u * n + v});
}

void solve() {
    sort(st.begin(), st.end());
    int u = 0, v = 0;
    for (auto [w, id] : st) {
        int x = id % n, y = id / n;
        if (x == u && y == v) continue;
        if (x > u) {
            swap(x, u);
            swap(y, v);
        }
        if (y < v) {
            swap(x, u);
            swap(y, v);
        }
        if (x == u && y == v) continue;
        if (x == u + 1 && y == v + 1) {
            ans += w;
            continue;
        }
        if (x == u + 1 && y == v) {
            ans += w;
            continue;
        }
        if (x == u && y == v + 1) {
            ans += w;
            continue;
        }
        add(u, v, w);
        add(u + 1, v + 1, w);
        add(u + 1, v, w);
        add(u, v + 1, w);
        u = x, v = y;
    }
}

