#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 200005

int n, m;
int a[MAXN];
int x[MAXN];
int ans[MAXN];

struct Node {
    int l, r;
    int val;
} seg[MAXN * 4];

void pushup(int u) {
    seg[u].val = seg[u << 1].val + seg[u << 1 | 1].val;
}

void build(int u, int l, int r) {
    seg[u].l = l;
    seg[u].r = r;
    if (l == r) {
        seg[u].val = a[l];
        return;
    }
    int mid = (l + r) >> 1;
    build(u << 1, l, mid);
    build(u << 1 | 1, mid + 1, r);
    pushup(u);
}

void update(int u, int l, int r, int v) {
    if (seg[u].l >= l && seg[u].r <= r) {
        seg[u].val += v;
        return;
    }
    int mid = (seg[u].l + seg[u].r) >> 1;
    if (l <= mid) update(u << 1, l, r, v);
    if (r > mid) update(u << 1 | 1, l, r, v);
    pushup(u);
}

int query(int u, int l, int r) {
    if (seg[u].l >= l && seg[u].r <= r) return seg[u].val;
    int mid = (seg[u].l + seg[u].r) >> 1;
    int res = 0;
    if (l <= mid) res += query(u << 1, l, r);
    if (r > mid) res += query(u << 1 | 1, l, r);
    return res;
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i < n; i++) scanf("%d", &a[i]);
    build(1, 1, n - 1);
    scanf("%d", &m);
    for (int i = 1; i <= m; i++) scanf("%d", &x[i]);
    for (int i = 1; i <= m; i++) {
        int res = 0;
        for (int j = 1; j < n; j++) {
            res += a[j];
            update(1, j + 1, n - 1, x[i] - a[j]);
        }
        res += query(1, 1, n - 1);
        ans[i] = res;
    }
    for (int i = 1; i <= m; i++) printf("%d\n", ans[i]);
    return 0;
}
