using namespace std;

const int N = 200000 + 10;

int n, m, a[N], x[N], ans[N];

struct Node {
    int l, r, v;
};

Node tree[N * 4];

void pushup(int u) {
    tree[u].v = max(tree[u * 2].v, tree[u * 2 + 1].v);
}

void build(int u, int l, int r) {
    tree[u].l = l;
    tree[u].r = r;
    if (l == r) {
        tree[u].v = a[l];
        return;
    }
    int mid = (l + r) >> 1;
    build(u * 2, l, mid);
    build(u * 2 + 1, mid + 1, r);
    pushup(u);
}

int query(int u, int l, int r) {
    if (tree[u].l >= l && tree[u].r <= r) {
        return tree[u].v;
    }
    int mid = (tree[u].l + tree[u].r) >> 1;
    int res = 0;
    if (l <= mid) res = max(res, query(u * 2, l, r));
    if (r > mid) res = max(res, query(u * 2 + 1, l, r));
    return res;
}

void modify(int u, int l, int v) {
    if (tree[u].l == tree[u].r) {
        tree[u].v = max(tree[u].v, v);
        return;
    }
    int mid = (tree[u].l + tree[u].r) >> 1;
    if (l <= mid) modify(u * 2, l, v);
    if (r > mid) modify(u * 2 + 1, l, v);
    pushup(u);
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    cin >> n;
    for (int i = 1; i < n; i++) {
        cin >> a[i];
    }
    build(1, 1, n - 1);
    cin >> m;
    for (int i = 1; i <= m; i++) {
        cin >> x[i];
        int res = 0;
        if (x[i] >= query(1, 1, n - 1)) {
            res = x[i] + a[n];
            modify(1, 1, x[i]);
            int l = 1, r = n - 1;
            while (l < r) {
                int mid = (l + r) >> 1;
                if (query(1, mid + 1, n - 1) >= x[i]) {
                    l = mid + 1;
                } else {
                    r = mid;
                }
            }
            res -= query(1, 1, l - 1);
        } else {
            res = x[i] + query(1, 1, n - 1);
        }
        ans[i] = res;
    }
    for (int i = 1; i <= m; i++) {
        cout << ans[i] << "\n";
    }
