class SegmentTree:
    def __init__(self, n):
        self.n = n
        self.tree = [0] * (4 * n)

    def update(self, node, l, r, i, v):
        if l == r:
            self.tree[node] = v
            return
        mid = (l + r) // 2
        if i <= mid:
            self.update(2 * node, l, mid, i, v)
        else:
            self.update(2 * node + 1, mid + 1, r, i, v)
        self.tree[node] = max(self.tree[2 * node], self.tree[2 * node + 1])

    def query(self, node, l, r, a, b):
        if a <= l and r <= b:
            return self.tree[node]
        mid = (l + r) // 2
        if b <= mid:
            return self.query(2 * node, l, mid, a, b)
        elif a >= mid + 1:
            return self.query(2 * node + 1, mid + 1, r, a, b)
        else:
            return max(self.query(2 * node, l, mid, a, b), self.query(2 * node + 1, mid + 1, r, a, b))

