    }
    long long ans = 1;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            int a = i & 1, b = j & 1;
            int c = (h - i - 1) & 1, d = (w - j - 1) & 1;
            ans = (ans * cnt[i][j][a][b] * cnt[i][j][c][d]) % MOD;
        }
    }
    printf("%lld\n", ans);
    return 0;
}
