import sys
input = sys.stdin.readline

mod = 10**9 + 7

def pow_mod(a, b):
    res = 1
    while b:
        if b & 1:
            res = res * a % mod
        a = a * a % mod
        b >>= 1
    return res

def solve():
    H, W = map(int, input().split())
    grid = [list(input().rstrip()) for _ in range(H)]

    cnt = [0] * 26
    for i in range(H):
        for j in range(W):
            cnt[ord(grid[i][j]) - ord('a')] += 1

    ans = 1
    for i in range(26):
        if cnt[i] % 2:
            ans = 0
            break
        ans = ans * pow_mod(2, cnt[i] // 2) % mod

    for i in range(H):
        for j in range(W // 2):
            if grid[i][j] != grid[i][W - 1 - j]:
                ans = 0
                break
        if not ans:
            break

    for j in range(W):
        for i in range(H // 2):
            if grid[i][j] != grid[H - 1 - i][j]:
                ans = 0
                break
        if not ans:
            break

    print(ans)

