int used[N][N];
int queue[N * N];
int front, rear;

void enqueue(int x) {
    queue[rear++] = x;
}

int dequeue() {
    return queue[front++];
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d", &u[i]);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &d[i]);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &l[i]);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &r[i]);
    }

    int x, y, dx, dy;
    for (int i = 0; i < n * n; i++) {
        x = i % n;
        y = i / n;
        dx = (x == 0) ? 1 : 0;
        dy = (y == 0) ? 1 : 0;
        enqueue(i);
        used[x][y] = 1;
    }

    while (front < rear) {
        int v = dequeue();
        x = v % n;
        y = v / n;
        for (int i = 0; i < u[x]; i++) {
            if (y > 0 && !used[x][y - 1]) {
                enqueue(v + n);
                used[x][y - 1] = 1;
            }
        }
        for (int i = 0; i < d[x]; i++) {
            if (y < n - 1 && !used[x][y + 1]) {
                enqueue(v + n);
                used[x][y + 1] = 1;
            }
        }
        for (int i = 0; i < l[y]; i++) {
            if (x > 0 && !used[x - 1][y]) {
                enqueue(v + 1);
                used[x - 1][y] = 1;
            }
        }
        for (int i = 0; i < r[y]; i++) {
            if (x < n - 1 && !used[x + 1][y]) {
                enqueue(v + 1);
                used[x + 1][y] = 1;
            }
        }
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (!used[i][j]) {
                printf("NO\n");
                return 0;
            }
        }
    }

    for (int i = 0; i < n * n; i++) {
        x = i % n;
        y = i / n;
        dx = (x == 0) ? 1 : 0;
        dy = (y == 0) ? 1 : 0;
        for (int j = 0; j < u[x]; j++) {
            if (y > 0 && used[x][y - 1]) {
                printf("U%d\n", x + 1);
                y--;
            }
        }
        for (int j = 0; j < d[x]; j++) {
            if (y < n - 1 && used[x][y + 1]) {
                printf("D%d\n", x + 1);
                y++;
            }
        }
        for (int j = 0; j < l[y]; j++) {
            if (x > 0 && used[x - 1][y]) {
                printf("L%d\n", y + 1);
                x--;
            }
        }
        for (int j = 0; j < r[y]; j++) {
            if (x < n - 1 && used[x + 1][y]) {
                printf("R%d\n", y + 1);
                x++;
            }
        }
    }

    return 0;
}
